sp_widget.client_script:
function($rootScope, $scope) {
	var c = this;
	c.selectItem = function(index){
		var id = c.data.notes[index].sys_id;
		console.log("Note ID: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updatedNote', function(event, data){
		//console.log("HSS1 " + data.note + " " + data.title + " " + data.noteID);
		c.data.notes.filter(function(note){
			note.noteID += "";
			data.noteID += "";
			//console.log("HSS3 "+ note.title + " " + note.note + " " + note.sys_id);
			if(note.noteID.indexOf(data.noteID)>=1){
				console.log("HSS2 " + " " +note.noteID + "--" + data.noteID+ " " +note.noteTitle + "--" + data.title+ " " +note.noteBody + "--" + data.note);

				note.noteTitle = data.title;
				note.noteBody = data.note;
			}
		});
	});

}

sp_widget.script:
(function() {
  data.notes = [];

	var noteGR = new GlideRecord("x_snc_createnotes_note");
	noteGR.addQuery("user", gs.getUser().getID());
	noteGR.orderByDesc("sys_created_on ");
	noteGR.query();

	while(noteGR.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'note, title, sys_id ');
		noteObj.note = noteGR.getValue("note").slice(0, 20);
		data.notes.push(noteObj);
	}

})();
